mapper.select:
SELECT CAM_NAME AS total_Base, count(*) AS total_count, AVG(ORIGINAL_ACC) AS total_original, AVG(SO_ACC) AS total_so, AVG(REAL_ACC) AS total_real FROM AI_STATISTICS WHERE MODEL_FLAG = 'Y' AND ADM_ID = #{sessionId}
AND cam_id = #{camId}
GROUP BY CAM_NAME

SELECT CAM_NAME AS total_Base, count(*) AS total_count, AVG(ORIGINAL_ACC) AS total_original, AVG(SO_ACC) AS total_so, AVG(REAL_ACC) AS total_real FROM AI_STATISTICS WHERE MODEL_FLAG = 'Y'
AND cam_id = #{camId}
GROUP BY CAM_NAME

SELECT TO_CHAR(TO_DATE(MMdate,'YYYY-MM'),'MM') AS TOTAL_BASE, COUNT(*) AS TOTAL_COUNT, ROUND(AVG(ORIGINAL_ACC), 2) AS TOTAL_ORIGINAL, ROUND(AVG(SO_ACC),2) AS TOTAL_SO, ROUND(AVG(REAL_ACC),2) AS TOTAL_REAL FROM ( SELECT CAM_ID, CAM_NAME, CAM_STATUS, ADM_ID, ADM_NAME, CAM_TYPE, TO_CHAR(CAM_CDATE,'YYYY-MM') AS MMdate, ADM_TYPE, TRAIN_METHOD, ORIGINAL_ACC, SO_ACC, REAL_ACC, MODEL_FLAG, TEST_CNT FROM AI_STATISTICS
MODEL_FLAG = 'Y' AND ADM_ID = #{sessionId}
AND CAM_CDATE < ROUND (to_date(to_char(TO_DATE(#{year},'yyyy'),'yyyy-mm-dd'),'yyyy-mm-dd'),'year') AND CAM_CDATE >= TRUNC (to_date(to_char(TO_DATE(#{year},'yyyy'),'yyyy-mm-dd'),'yyyy-mm-dd'),'year')
) A GROUP BY MMdate
