mapper.select:
select MODEL.TRAIN_METHOD as "train_method", MODEL.ORIGINAL_ACC as "original_acc", MODEL.SO_ACC as "so_acc", MODEL.MODEL_DIR as "model_dir", MODEL.MODEL_NAME as "model_name" from AI_CAMPAIGN CAMP, AI_TRAIN_MODEL MODEL where CAMP.CAM_ID = MODEL.CAM_ID
AND MODEL.CAM_ID = #{cam_id}
ORDER BY MODEL.SO_ACC DESC

select CAMP.CAM_ID as "cam_id", FEATURE.TRAIN_METHOD as "train_method", FEATURE.FEATURE_NAME as "feature_name", FEATURE.WEIGHT as "weight" from AI_CAMPAIGN CAMP, AI_TRAIN_MODEL MODEL, AI_TRAIN_MODEL_FEATURE FEATURE where CAMP.CAM_ID = MODEL.CAM_ID AND CAMP.CAM_ID = FEATURE.CAM_ID AND MODEL.TRAIN_METHOD = FEATURE.TRAIN_METHOD
AND CAMP.CAM_ID = #{cam_id}
AND ROWNUM <= 10 order by FEATURE.WEIGHT desc
