mapper.select:
select st_seq, cam_id, st_c1, st_c2, st_c3, st_c4, st_c5, st_c6, st_c7, st_c8, st_c9, st_c10, st_c200  
   from (		
        select   rownum as rnum  , A.* 
        from       
        (
        select 
			*  
		from 
			ai_staging_train
		where  
			cam_id = #{cam_id} order by st_seq desc
        ) A
        where  rownum <= #{page_end}  
    ) B
where b.rnum >= #{page_st}
