mapper.select:
select * 
   from (		
        select   rownum as rnum  , A.* 
        from       
        (
        select 
			*  
		from 
			ai_staging_train
		where  
			cam_id = '226' order by st_seq desc
        ) A
        where  rownum <= #{page_end}  
    ) B
where b.rnum >= #{page_st}
